/**
 * Function to swap two elements in an array.
 * @param {Array} array - The array in which elements are to be swapped.
 * @param {number} element1 - The index of the first element to be swapped.
 * @param {number} element2 - The index of the second element to be swapped.
 * @returns {Array} - The array with swapped elements.
 */
function swap(array, element1, element2) {
    // Swap the elements using a temporary variable
    let temp = array[element1];
    array[element1] = array[element2];
    array[element2] = temp;
    // Return the modified array
    return array;
}

// Export the swap function to be used in other modules
module.exports = swap;
